import Head from 'next/head'
import styles from '../styles/Home.module.css'
import React from 'react'
import {InputGroup, FormControl, Button, Card} from 'react-bootstrap'
import getPackageData from '../lib/packageData'
import data from '../testData/data.json'

export async function getStaticProps(){
    const featuredPackageData = data
    return {
        props: {
            featuredPackageData
        }
    }
}

export default class Trust extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            searchValue: '',
        }

    }

    featuredPackages(){
        return(
            this.props.featuredPackageData.map((p) => {
                return(
                // console.log("item:", p.title )
                // <div>{p.title}</div>
                <Card style={{ width: '18rem' }}>
                <Card.Body>
                <Card.Title>{p.title}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">Trustscore: {p.trustScore}</Card.Subtitle>
                <Card.Text>
                {p.dependencies.map((dp) => {
                    console.log(dp.dependency)
                    return( 
                        <span>{dp.dependency} </span>
                        )
                }
                )}
                </Card.Text>
                <Card.Link href="#">Card Link</Card.Link>
                <Card.Link href="#">Another Link</Card.Link>
                </Card.Body>
                </Card>
                )
            })
            )
        }

        handleChange = (event) =>{
            this.setState({searchValue: event.target.value})
        }
        handleSubmit = () =>{
            if(this.state.searchValue){
                alert('Searching for ' + this.state.searchValue);
                event.preventDefault()
            }
        }

        render(){
            // console.log(this.props.featuredPackageData)
            return (
                <div className={styles.container}>
                <Head>
                <title>TrustSECO</title>
                <link rel="icon" href="/favicon.ico" />
                </Head>

                <main className={styles.main}>
                <h1 className={styles.title}>
                Trust SECO
                </h1>

                <p className={styles.description}>
                A project by <a href="https://secureseco.org">SecureSECO</a>
                </p>

                <InputGroup className="mb-3">
                <FormControl
                value = {this.state.value} 
                onChange = {this.handleChange}
                placeholder="Package"
                aria-label="Package"
                aria-describedby="Package"
                />
                <InputGroup.Append>
                <Button variant="outline-secondary" onClick={this.handleSubmit}>Search</Button>
                </InputGroup.Append>
                </InputGroup>
                <p>{this.state.searchValue}</p>

                <div>
                {this.featuredPackages()} 
                </div>
                <div>
                    {/* //{this.props.featuredPackageData} */}
                </div>
                </main>

                <footer className={styles.footer}>
                </footer>
                </div>
                )
            }
        }
