import Head from 'next/head'
import styles from '../styles/Home.module.css'
import React from 'react'
import {InputGroup, FormControl, Button, Card} from 'react-bootstrap'
import getPackageData from '../lib/packageData'

async function getStaticProps(){
    const featuredPackageData = getPackageData()
    return {
        props: {
            featuredPackageData
        }
    }
}

function featuredPackages({featuredPackageData}){
    return(
        featuredPackageData.map((data) => {
            <Card style={{ width: '18rem' }}>
            <Card.Body>
            <Card.Title>{data.title}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">Card Subtitle</Card.Subtitle>
            <Card.Text>
            Some quick example text to build on the card title and make up the bulk of
            the card's content.
            </Card.Text>
            <Card.Link href="#">Card Link</Card.Link>
            <Card.Link href="#">Another Link</Card.Link>
            </Card.Body>
            </Card>
        })
        )
    }

    export default class Trust extends React.Component {
        constructor(props){
            super(props);
            this.state = {
                searchValue: '',
            }

        }
        handleChange = (event) =>{
            this.setState({searchValue: event.target.value})
        }
        handleSubmit = () =>{
            alert('Searching for ' + this.state.searchValue);
            event.preventDefault()
        }

        render(){
            return (
                <div className={styles.container}>
                <Head>
                <title>TrustSECO</title>
                <link rel="icon" href="/favicon.ico" />
                </Head>

                <main className={styles.main}>
                <h1 className={styles.title}>
                Trust SECO
                </h1>

                <p className={styles.description}>
                A project by <a href="https://secureseco.org">SecureSECO</a>
                </p>

                <InputGroup className="mb-3">
                <FormControl
                value = {this.state.value} 
                onChange = {this.handleChange}
                placeholder="Package"
                aria-label="Package"
                aria-describedby="Package"
                />
                <InputGroup.Append>
                <Button variant="outline-secondary" onClick={this.handleSubmit}>Search</Button>
                </InputGroup.Append>
                </InputGroup>
                <p>{this.state.searchValue}</p>

                <div>
                <Card style={{ width: '18rem' }}>
                <Card.Body>
                <Card.Title>Package</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">Trustscore: ..</Card.Subtitle>
                <Card.Text>
                Dependencies:
                </Card.Text>
                <Card.Link href="#">Card Link</Card.Link>
                <Card.Link href="#">Another Link</Card.Link>
                </Card.Body>
                </Card>
                {this.featuredPackages}
                </div>
                </main>

                <footer className={styles.footer}>
                </footer>
                </div>
                )
            }
        }
